/* ##### DOM 選擇和初始顯示 ##### */  
/* 選擇一些將被操作的 DOM 元素 */  

const featuresContent = document.querySelector("#features .content");  
const testimonialCard = document.querySelector(
  "#testimonials .testimonial-card"
);
const prevBtn = document.querySelector("#testimonials .prev-btn");
const nextBtn = document.querySelector("#testimonials .next-btn");
const pricingContent = document.querySelector("#pricing .content");
const menuIcon = document.querySelector(".menu-icon");
const mobileNavMenu = document.querySelector(".mobile-nav-menu");
const navLinks = document.querySelectorAll(".nav-link");
let currentTestimonialIndex = 0;


/* ##### 顯示特性列表 ##### */  
/* 將特性列表顯示在頁面上 */

const displayFeatures = () => {
  featuresList.forEach((f) => {
    const html = `




${f.title}


        ${f.description}
      `;

    const featureCard = document.createElement("div");
    featureCard.classList.add("feature-card");
    featureCard.innerHTML = html;

    featuresContent.appendChild(featureCard);
  });
};


displayFeatures();


/* ##### 顯示客戶評價 ##### */
/* 顯示當前的客戶評價 */ 

const displayTestimonial = () => {
  const html = `





 ${testimonialsList[currentTestimonialIndex].review}












${testimonialsList[currentTestimonialIndex].name}

${testimonialsList[currentTestimonialIndex].designation}

`;

  testimonialCard.innerHTML = html;
  testimonialCard.classList.add("active");
};

displayTestimonial();


/* ##### 切換客戶評價 ##### */
/* 為前後按鈕添加點擊事件，切換當前顯示的評價 */   


nextBtn.addEventListener("click", () => {
  testimonialCard.classList.remove("active");

  setTimeout(() => {
    currentTestimonialIndex++;
    if (currentTestimonialIndex >= testimonialsList.length) {
      currentTestimonialIndex = 0;
    }
    displayTestimonial();
  }, 200);
});



prevBtn.addEventListener("click", () => {
  testimonialCard.classList.remove("active");

  setTimeout(() => {
    currentTestimonialIndex--;
    if (currentTestimonialIndex < 0) {
      currentTestimonialIndex = testimonialsList.length - 1;
    }
    displayTestimonial();
  }, 200);
});


/* ##### 顯示定價計劃 ##### */
/* 將定價計劃顯示在頁面上。  */  


const displayPricing = () => {
  plans.forEach((p) => {
    const featuresHTML = p.features
      .map(
        (f) =>
          `
${f}`
      )
      .join("");

    const html = `
${p.name}

      ${featuresHTML}


${p.price}
    Choose`;

    const plan = document.createElement("div");
    plan.classList.add("plan");
    plan.innerHTML = html;

    pricingContent.appendChild(plan);
  });
};

displayPricing();


/* ##### 移動導航菜單 ##### */
/* 為菜單圖標和導航鏈接添加點擊事件，實現移動導航菜單的顯示和隱藏 */

menuIcon.addEventListener("click", () => {
  mobileNavMenu.classList.toggle("active");
});



navLinks.forEach((link) => {
  link.addEventListener("click", (e) => {
    e.preventDefault();
    const targetId = link.getAttribute("href");
    const targetElement = document.querySelector(targetId);

    if (targetElement) {
      const offset = targetElement.offsetTop - 60;
      window.scrollTo({ top: offset });
    }

    mobileNavMenu.classList.remove("active");
  });
});


/* !!!!! 這段 JavaScript 代碼主要實現了從 Blogger API 獲取最新的文章並生成 HTML 結構來顯示在網頁上。讓我們逐步解析這段代碼 !!!!! */


/* ##### DOM 選擇器 ##### */
/* 這行代碼選擇了頁面上的一個元素，它的 ID 是 blog，並且這個元素中有一個類名為 latest-posts-list 的元素。這個元素將用來顯示最新的文章 */

const latestPostsList = document.querySelector('#blog .latest-posts-list');


/* ##### 生成最新文章的函數 ##### */  
/* 
   這個函數 generateLatestPosts 接受一個文章數組（posts），然後對每篇文章進行以下操作：
   01.提取標題：const title = post.title.$t;
   02.處理縮略圖：如果文章有縮略圖，則替換縮略圖的 URL，以獲取更高分辨率的圖片。如果沒有縮略圖，則使用默認縮略圖（這裡默認縮略圖的 URL 是 #）。
   03.提取摘要：const summary = post.summary.$t;
   04.提取文章鏈接：const link = post.link[post.link.length - 1].href;
 */  


const generateLatestPosts = (posts) => {
	posts.forEach(post => {
    	const title = post.title.$t;
      	const defaultThumbnail = '#';
      	const thumbnailSrc = post.media$thumbnail ? post.media$thumbnail.url.replace('/s72', '/s300') : defaultThumbnail;
      	const summary = post.summary.$t;
      	const link = post.link[post.link.length - 1].href;

      /* 然後，生成包含縮略圖、標題、摘要和 “閱讀更多” 按鈕的 HTML 結構： */
      /* 創建一個新的 div 元素，並將生成的 HTML 結構插入這個 div 中，然後將這個 div 添加到 latestPostsList 元素中。*/

      const html = `








${title}



              ${summary}


            Read more`;

      	const postDiv = document.createElement('div');
      	postDiv.classList.add('post');

      	postDiv.innerHTML = html;
      	latestPostsList.appendChild(postDiv);
   }); 
}  

/* ##### 處理最新文章的函數 ##### */
/* 這個函數 handleLatestPosts 接受一個 posts 對象，並將其中的文章條目（posts.feed.entry）傳遞給 generateLatestPosts 函數來生成並顯示新文章。*/

const handleLatestPosts = (posts) => {
  	generateLatestPosts(posts.feed.entry);
}


/*

整段代碼解析
這段代碼首先選擇頁面上用來顯示最新文章的元素，然後定義了兩個函數：

generateLatestPosts：負責生成最新文章的 HTML 結構並插入到頁面中。
handleLatestPosts：從給定的 posts 對象中提取文章條目並傳遞給 generateLatestPosts。
這段代碼假設你已經通過某種方式從 Blogger API 獲取了最新的文章，並將這些文章數據傳遞給 handleLatestPosts 函數來顯示。

*/
